import React, { Component } from 'react';

export default class One extends Component {
    state = {
        list: [],
        isShow: false,
    };
    render() {
        return (
            <div className="one-box">
                <ul>
                    <li>
                        <span>姓名</span>
                        <span>密码</span>
                        <span>状态</span>
                    </li>
                    {this.state.list.map(item => {
                        return (
                            <li key={item.id}>
                                <span>{item.username}</span>
                                <span>{item.password}</span>
                                <span>
                                    <b onClick={this.headleEdit.bind(this, item)}>编辑</b>
                                    <b onClick={this.headleDel.bind(this, item.id)}>删除</b>
                                </span>
                            </li>
                        );
                    })}
                </ul>
                {this.state.isShow ? <div className="mask-box"></div> : ''}
            </div>
        );
    }
    async componentDidMount() {
        let res = await this.http('/getlist');
        this.setState({
            list: res.data.result,
        });
    }
    async headleDel(id) {
        let res = await this.http('/del', 'post', { id });
        alert(res.data.msg);
        if (res.data.code === 1) {
            let res = await this.http('/getlist');
            this.setState({
                list: res.data.result,
            });
        }
    }
    async headleEdit(item) {
        this.setState({
            isShow: true,
        });
        const { username, password, id } = item;
        let res = await this.http('/edit', 'put', { username, password, id });
        console.log(res);
    }
}
